Scripts.OnTabPressed:
if (IsShiftKeyDown()) then
  NoxConfigFrameTooltipEditBox_Prev(self);
else
  NoxConfigFrameTooltipEditBox_Next(self);
end

Scripts.OnClick:
if (self:GetChecked()) then
  PlaySound("igMainMenuOptionCheckBoxOff");
else
  PlaySound("igMainMenuOptionCheckBoxOn");
end
NoxConfigFrameCheckButton_OnClick(self);

PlaySound("igCharacterInfoTab");
PanelTemplates_Tab_OnClick(self, NoxConfigFrame);
NoxConfigFrame.currentPanel:Hide();
NoxConfigFrame.currentPanel = self.panel;
NoxConfigFrame.currentPanel:Show();

Scripts.OnShow:
for i = 1, 30 do
  local left = _G["NoxConfigTooltip" .. i .. "LeftText"];
  local right = _G["NoxConfigTooltip" .. i .. "RightText"];
  left:ClearAllPoints();
  left:SetPoint("TOPLEFT", "NoxConfigFrameTooltipContentGroup", "TOPLEFT", 10, (-16 * i) + 8); 
  left:SetPoint("TOPRIGHT", "NoxConfigFrameTooltipContentGroup", "TOP", -1, (-16 * i) + 8); 
  right:ClearAllPoints();
  right:SetPoint("TOPLEFT", "NoxConfigFrameTooltipContentGroup", "TOP", 1, (-16 * i) + 8); 
  right:SetPoint("TOPRIGHT", "NoxConfigFrameTooltipContentGroup", "TOPRIGHT", -10, (-16 * i) + 8); 
end

if (not NoxMover) then
  PanelTemplates_SetNumTabs(self, 3);
  NoxConfigFrameTab4:Hide();
end
NoxConfigFrame_OnShow(self);
